body.raw:
{
  "context": {
    "version": "{{version}}",
    "action": "rating_ledger",
    "domain": "{{domain}}",
    "location": {
      "country": {
        "code": "IND"
      },
      "city": {
        "code": "std:080"
      }
    },
    "bap_id": "{{bap_id}}",
    "bap_uri": "{{bap_uri}}",
    "bpp_id": "{{bpp_id}}",
    "bpp_uri": "{{bpp_uri}}",
    "transaction_id": "{{transaction_id}}",
    "message_id": "{{$guid}}",
    "timestamp": "{{timestamp}}",
    "ttl": "PT30S",
    "schema_context": [
      "https://raw.githubusercontent.com/beckn/protocol-specifications-new/refs/heads/main/schemas/charging_service/v1/context.jsonld"
    ]
  },
  "message": {
    "ledger": {
      "@context": "https://becknprotocol.io/schemas/core/v2/Ledger/schema-context.jsonld",
      "@type": "beckn:Ledger",
      "beckn:id": "rating-ledger-{{$guid}}",
      "beckn:type": "RATING",
      "beckn:entity": {
        "@type": "beckn:Entity",
        "beckn:type": "bap",
        "beckn:id": "{{bap_id}}"
      },
      "beckn:reference": {
        "@type": "beckn:Reference",
        "beckn:orderId": "order-123456",
        "beckn:fulfillmentId": "fulfillment-001"
      },
      "beckn:rating": {
        "@context": "https://becknprotocol.io/schemas/core/v2/Rating/schema-context.jsonld",
        "@type": "beckn:Rating",
        "beckn:value": 5,
        "beckn:best": 5,
        "beckn:worst": 1,
        "beckn:category": "fulfillment"
      },
      "beckn:feedback": {
        "@context": "https://becknprotocol.io/schemas/core/v2/Feedback/schema-context.jsonld",
        "@type": "beckn:Feedback",
        "beckn:comments": "Excellent charging experience! The station was clean, easy to find, and the charging was fast and reliable.",
        "beckn:tags": ["fast-charging", "easy-to-use", "clean-station", "helpful-staff"]
      },
      "beckn:timestamp": "{{timestamp}}"
    }
  }
}
