{
  "context": {
    "version": "{{version}}",
    "action": "on_select",
    "domain": "{{domain}}",
    "location": {
      "country": {
        "code": "IND"
      },
      "city": {
        "code": "std:080"
      }
    },
    "bap_id": "{{bap_id}}",
    "bap_uri": "{{bap_uri}}",
    "bpp_id": "{{bpp_id}}",
    "bpp_uri": "{{bpp_uri}}",
    "transaction_id": "{{transaction_id}}",
    "message_id": "{{$guid}}",
    "timestamp": "{{timestamp}}",
    "ttl": "PT30S",
    "schema_context": [
      "https://raw.githubusercontent.com/beckn/protocol-specifications-new/refs/heads/main/schemas/charging_service/v1/context.jsonld"
    ]
  },
  "message": {
    "order": {
      "@context": "https://becknprotocol.io/schemas/core/v2/Order/schema-context.jsonld",
      "@type": "beckn:Order",
      "beckn:id": "order-ev-charging-001",
      "beckn:orderStatus": "QUOTE_REQUESTED",
      "beckn:orderNumber": "ORD-2025-001",
      "beckn:seller": "ecopower-charging",
      "beckn:buyer": "user-12345",
      "beckn:orderValue": {
        "currency": "INR",
        "value": 100.0,
        "components": [
          {
            "type": "UNIT",
            "value": 45.0,
            "currency": "INR",
            "description": "Charging session cost (2.5 kWh @ ₹18.00/kWh)"
          },
          {
            "type": "FEE",
            "value": 10.0,
            "currency": "INR",
            "description": "Service fee"
          },
          {
            "type": "FEE",
            "value": 2.5,
            "currency": "INR",
            "description": "Buyer finder fee (2.5%)"
          },
          {
            "type": "SURCHARGE",
            "value": 5.0,
            "currency": "INR",
            "description": "Surge price (10%)"
          },
          {
            "type": "DISCOUNT",
            "value": -10.0,
            "currency": "INR",
            "description": "Promotional discount"
          }
        ]
      },
      "beckn:orderItems": [
        {
          "beckn:lineId": "line-001",
          "beckn:orderedItem": "ev-charger-ccs2-001",
          "beckn:quantity": 2.5,
          "beckn:acceptedOffer": {
            "@context": "https://becknprotocol.io/schemas/core/v2/Offer/schema-context.jsonld",
            "@type": "beckn:Offer",
            "beckn:id": "offer-ccs2-60kw-kwh",
            "beckn:descriptor": {
              "name": "Per-kWh Tariff - CCS2 60kW"
            },
            "beckn:price": {
              "currency": "INR",
              "value": 18.0,
              "applicableQuantity": {
                "unitText": "Kilowatt Hour",
                "unitCode": "KWH",
                "unitQuantity": 1
              }
            },
            "beckn:validity": {
              "@type": "beckn:TimePeriod",
              "schema:startDate": "2024-10-01T00:00:00Z",
              "schema:endDate": "2025-01-15T23:59:59Z"
            },
            "beckn:acceptedPaymentMethod": ["UPI", "Card", "Wallet"],
            "beckn:offerAttributes": {
              "@type": "ChargingOffer",
              "buyerFinderFee": {
                "feeType": "PERCENTAGE",
                "feeValue": 2.5
              },
              "idleFeePolicy": "₹2/min after 10 min post-charge"
            }
          }
        }
      ],
      "beckn:fulfillment": {
        "@context": "https://becknprotocol.io/schemas/core/v2/Fulfillment/schema-context.jsonld",
        "@type": "beckn:Fulfillment",
        "beckn:id": "fulfillment-charging-001",
        "beckn:mode": "RESERVATION",
        "beckn:status": "PENDING",
        "beckn:deliveryAttributes": {
          "@type": "ChargingSession",
          "sessionStatus": "PENDING",
          "authorizationMode": "APP_QR",
          "connectorType": "CCS2",
          "maxPowerKW": 60,
          "reservationId": "RESV-984532",
          "gracePeriodMinutes": 10
        }
      },
      "beckn:payment": {
        "@context": "https://becknprotocol.io/schemas/core/v2/Payment/schema-context.jsonld",
        "@type": "beckn:Payment",
        "beckn:status": "NOT_PAID",
        "beckn:amount": {
          "currency": "INR",
          "value": 100.0
        },
        "beckn:acceptedPaymentMethods": [
          "schema:BankTransfer",
          "schema:UPI",
          "schema:Wallet"
        ]
      },
      "beckn:orderAttributes": {
        "@type": "ChargingSession",
        "buyerFinderFee": {
          "feeType": "PERCENTAGE",
          "feeValue": 2.5
        }
      }
    }
  }
